SizeF size = font.MeasureString(text, new SizeF(100,100) , format);
//Draw string to PDF page.
graphics.DrawString(text, font, PdfBrushes.Black, new RectangleF(PointF.Empty, size), format);
//Save the document.
document.Save("Output.pdf");
//Close the document.
document.Close(true);

